   private void btnAddStudentFileMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnAddStudentFileMouseClicked
      List<SinhVien> sinhViens = new ArrayList<>();
      FileDialog dialog = new FileDialog((Dialog) null, "Select IMG");
      dialog.setMode(FileDialog.LOAD);
      dialog.setVisible(true);
      try {
         BufferedReader reader = new BufferedReader(new FileReader(dialog.getDirectory() + dialog.getFile(), StandardCharsets.UTF_8));
         reader.readLine();
         while (reader.ready()) {
            String line = reader.readLine();
            SinhVien studentTmp = new SinhVien();
            String[] parts = line.split(",");
            studentTmp.setMssv(parts[0]);
            studentTmp.setHoVaTen(parts[1]);
            studentTmp.setGioiTinh(parts[2]);
            studentTmp.setCmnd(parts[3]);
            studentTmp.setMaLop(dialog.getFile()
                                      .replace(".csv", ""));
            sinhViens.add(studentTmp);
         }
         reader.close();
      } catch (FileNotFoundException e) {
         System.out.println("File not found!");
      } catch (IOException e) {
         e.printStackTrace();
      }
      Session session = HibernateUtils.getSessionFactory()
                                      .openSession();
      session.getTransaction()
             .begin();
      SinhVienDAOImpl.setSession(session);
      LopHocDAOImpl.setSession(session);
      LopHocDAO lopHocDAO = new LopHocDAOImpl();
      LopHoc lopHoc = new LopHoc();
      lopHoc.setMaLop(dialog.getFile()
                            .replace(".csv", ""));
      lopHocDAO.insert(lopHoc);
      session.flush();
      SinhVienDAO sinhVienDAO = new SinhVienDAOImpl();
      sinhVienDAO.insertAll(sinhViens);
      session.getTransaction()
             .commit();
      session.close();
      showListStudent();
   }//GEN-LAST:event_btnAddStudentFileMouseClicked

   private void showListStudent() {
      Session session = HibernateUtils.getSessionFactory()
                                      .openSession();
      session.getTransaction()
             .begin();
      SinhVienDAOImpl.setSession(session);
      SinhVienDAO sinhVienDAO = new SinhVienDAOImpl();
      List<SinhVien> sinhVienList = sinhVienDAO.getAll();
      for (int i = 0; i < sinhVienList.size(); i++) {
         SinhVien sinhVien = sinhVienList.get(i);
         tableListStudent.getModel()
                         .setValueAt(i + "", i, 0);
         tableListStudent.getModel()
                         .setValueAt(sinhVien.getMssv(), i, 1);
         tableListStudent.getModel()
                         .setValueAt(sinhVien.getHoVaTen(), i, 2);
         tableListStudent.getModel()
                         .setValueAt(sinhVien.getGioiTinh(), i, 3);
         tableListStudent.getModel()
                         .setValueAt(sinhVien.getCmnd(), i, 4);
         tableListStudent.getModel()
                         .setValueAt(sinhVien.getMaLop(), i, 5);
      }
      session.close();
   }

   private void btnSaveAddStudentMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnSaveAddStudentMouseClicked
      String mssv = textFieldMSSV.getText();
      String name = textFieldName.getText();
      String cmnd = textFieldCMND.getText();
      String maLop = (String) boxClassAddStudent.getSelectedItem();
      String gioiTinh;
      if (radioMale.isSelected())
         gioiTinh = "Nam";
      else
         gioiTinh = "Nữ";
      SinhVien sinhVien = new SinhVien(mssv, name, gioiTinh, cmnd, maLop);
      Session session = HibernateUtils.getSessionFactory()
                                      .openSession();
      session.getTransaction()
             .begin();
      SinhVienDAOImpl.setSession(session);
      SinhVienDAO sinhVienDAO = new SinhVienDAOImpl();
      sinhVienDAO.insert(sinhVien);
      session.getTransaction()
             .commit();
      session.close();
      showListStudent();
   }//GEN-LAST:event_btnSaveAddStudentMouseClicked

